	adaptacoesMenuMobile() {
 
	   if(!ILUMINIM_UTILS.screen.isMobile()){
		  return;
	   }
 
	   function abrirSubMenu() {
 
		  $(document).on('click', '#menu-mobile a', function (e) {
 
			 let target_ul_parent = $(this).parent().parent('ul');
 
			 let target_sub_menu = $(this).siblings('ul');
 
			 if (target_sub_menu.length > 0 && !target_sub_menu.hasClass('menu__nivel-quatro')) {
 
				e.preventDefault();
 
				target_sub_menu.addClass('sub-menu-open');
 
				target_ul_parent.animate({ scrollTop: 0 }, 400);
 
				$(this).parents('#menu-lateral').animate({ scrollTop: 0 }, 400);
 
			 }
 
		  });
 
	   }
 
	   function abrirMenu() {
 
		  $(document).on('click', '#cabecalho .conteudo-atalho-menu-bars', function (e) {
 
			 $('div#menu-mobile').addClass('show-menu');
			 $('body').addClass('show-menu-mobile');
 
			 $('.iluminim-backdrop').addClass('active');
 
		  });
 
	   }
 
	   function fecharMenu() {
 
		  $(document).on('click', '#menu-mobile .close-menu-mobile', function () {
 
			 ILUMINIM_UTILS.fecharMenuMobile();
 
		  });
 
	   }
 
	   function fecharSubMenu() {
		  $(document).on('click', '#menu-mobile li.menu__item.voltar-sub-menu a', function () {
 
			 $(this).parent().parent('ul.sub-menu-open').removeClass('sub-menu-open');
 
		  });
	   }
 
	   $('div#menu-mobile').append(`
		  <div class="close-menu-mobile">
				<div class="close-menu-icon">×</div>
		  </div>
	   `);
 
	   $('#menu-mobile a').each(function () {
 
		  let link = $(this).attr('href');
		  let nome = $(this).text();
		  let target_sub_menu = $(this).siblings('ul.menu__nivel-tres');
 
		  if (target_sub_menu.length > 0) {
 
			 target_sub_menu.prepend(`
				   <li class="menu__title">
					  <a href="${link}">
							${nome}
					  </a>
				   </li>
 
				   <li class="menu__item voltar-sub-menu">
					  <a href="javascript:void(0);">
							<i class="icon-angle-left"></i>
							Voltar
					  </a>
				   </li>
				`);
 
		  }
	   });
 
 
	   abrirMenu();
	   abrirSubMenu()
	   fecharMenu();
	   fecharSubMenu();
 
	},
 
